import json
from utlis.file.FileUtil import file_utils
from deepdiff import DeepDiff

class handle_json(object):


    '''读取json文件信息'''
    def read_json(self,file_name=None):
        if file_name==None:
            return None
        with open(file_utils.location_file(file_name),encoding='utf-8') as info:
            data = json.load(info)

        return data


    '''根据json中字段获取值'''
    def get_data_value(self,file_name,key):
        data =self.read_json(file_name=file_name)
        return data[key]

    '''根据json中字段获取值'''
    def set_data_value(self,file_name,key,key_data):
        data =self.read_json(file_name=file_name)
        data[key] = key_data
        self.write_json(data)

        return data[key]


    '''写入json信息'''
    def write_json(self,data,file_name=None):
        data_value = json.dumps(data,indent=4)
        if file_name!=None:
            with open(file_utils.location_file(file_name),"w") as info:
                info.write(data_value)



    #利用 deepdiff 包 对json格式检查
    def check_json_format(self,dict_expected,dict_reality):
        if isinstance(dict_expected,dict) and isinstance(dict_reality,dict):
            cmp_dict = DeepDiff(dict_expected,dict_reality,ignore_order=True).to_dict()
            if cmp_dict.get('dictionary_item_added'):
                print('json格式错误 预期:{0} 实际:{1}'.format(cmp_dict.get('dictionary_item_removed'),cmp_dict.get('dictionary_item_added')))
                return False
            else:
                return True
        print('参数错误参数必须全部是标准json格式')
        return False
handle_json = handle_json()


